#include <bits/stdc++.h>
using namespace std;

class ChristmasCrackers {
public:
	vector <int> crack(int N, int K) {
		vector<int> ans(N * K);
		for (int i = 0; i < N * K; ++i) ans[i] = i % N;
		if ((N * K) % 2 == 1) ans.push_back(0);
		return ans;
	}
};


